			key,
			value,
			leftData = params.leftItem.data,
			topData = params.topItem.data,
			leftKeys,topKeys,i,len;
		
		xw.writeStartDocument();
		xw.writeStartElement('trqi');
		
		leftKeys = Object.keys(leftData);
		i=0;
		len = leftKeys.length;
